    private void loginButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginButtonActionPerformed

       String username = usernameBox.getText();
       String password = new String (passwordBox.getPassword());

   try {
       File userData = new File("userData.txt");
       Scanner sc = new Scanner(userData);
       while (sc.hasNextLine()) {
           String userName = sc.nextLine();
           String passWord = sc.nextLine();
           
           if (username.equals(userName) && password.equals(passWord)) {
               dispose();
               DashBoardFrame DF = new DashBoardFrame();
               DF.setLocationRelativeTo(null);
               DF.setVisible(true);
               break;
           }
           else{
               JOptionPane.showMessageDialog(null, "Username and Password didnot matchedd", "Error", JOptionPane.ERROR_MESSAGE);
           }
       }
       }catch(FileNotFoundException e){
           System.out.println(""+e);
       }
    }//GEN-LAST:event_loginButtonActionPerformed

